import React, { Component, Fragment } from 'react';
import gql from 'graphql-tag';
import { Query } from 'react-apollo';
import { Link } from 'react-router-dom';

const PITCH_QUERY = gql`
  query PitchQuery($id: Int!) {
    pitch(where: {id: {_eq: $id}}) {
      category
      desc
      owner
      title
    }
  }
`;

export class Launch extends Component {
  render() {
    let { id } = this.props.match.params;
    id = parseInt(id);
    return (
      <Fragment>
        <Query query={PITCH_QUERY} variables={{ id }}>
          {({ loading, error, data }) => {
            if (loading) return <h4>Loading...</h4>;
            if (error) console.log(error);
            if(data) console.log(data);

            const {
              category,
              desc,
              owner,
              
            } = data.pitch;
            console.log(data.pitch[0]);
            const title = data.pitch.title;

            return (
              <div>
                <h1 className="display-4 my-3">
                  <span className="text-dark">Title:</span> {title}
                </h1>
                <h4 className="mb-3">The details</h4>
                <ul className="list-group">
                  <li className="list-group-item">
                    Owner: {owner}
                  </li>
                  <li className="list-group-item">
                    Category: {category}
                  </li>
                  <li className="list-group-item">
                    Description: {desc}
                  </li>
                </ul>
                <hr />
                <Link to="/home" className="btn btn-secondary">
                  Back
                </Link>
              </div>
            );
          }}
        </Query>
      </Fragment>
    );
  }
}

export default Launch;
