import React from 'react';
import { Button, Modal, ModalHeader, ModalBody, ModalFooter } from 'reactstrap';

class GDPRModal extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      modal: false
    };

    this.toggle = this.toggle.bind(this);
  }

  toggle() {
    this.setState(prevState => ({
      modal: !prevState.modal
    }));
  }

  render() {
    return (
      <div>
        <Button color="info" onClick={this.toggle}>Privacy policy{this.props.buttonLabel}</Button>{' '}
        <Modal isOpen={this.state.modal} toggle={this.toggle} className={this.props.className}>
          <ModalHeader toggle={this.toggle}>Our data privacy policy</ModalHeader>
          <ModalBody>
          Pitch App Privacy policy explained
          </ModalBody>
          <ModalFooter>
            <Button color="success" onClick={this.toggle}>I agree</Button>{' '}
            <Button color="danger" onClick={this.toggle}>I do not agree</Button>
          </ModalFooter>
        </Modal>
      </div>
    );
  }
}

export default GDPRModal;
